    private void jButtonKAYDETActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonKAYDETActionPerformed

        
        String ad = AD.getText();
        String soyad =Soyad.getText();
        String mail = Eposta.getText();
        String kullaniciAdi = KullaniciAdi.getText();
        String mobilNo = TelefonNo.getText();
        String sifre = Parola.getSelectedText();
        String il =il.getSelectedItem()+ "";
        String ilce = ilce.getSelectedItem() + "";
        String Adres = Adres.getText() + " " + ilce.getSelectedItem() + " - " + il.getSelectedItem();
        
        System.err.println(Adres);
        if (ad.equals("") || soyad.equals("") || mail.equals("") || kullaniciAdi.equals("") || sifre.equals("") || Adres.equals("")) {
            JOptionPane.showMessageDialog(rootPane, "Boş alanları doldurunuz");
        } else {
            try {
                boolean value = Yonetici.isAvailable(kullaniciAdi);
                if (value == false) {
                    Yonetici.yonetici(ad, soyad, kullaniciAdi, sifre, mail,il, ilce, mobilNo, Adres);
                    JOptionPane.showMessageDialog(null,
                            "Admin oluşturuldu");
                  
                } else {
                    JOptionPane.showMessageDialog(null,
                            "Bu kullanıcı adı daha önce alınmış");
                }

            } catch (Exception exception) {
                exception.printStackTrace();
                JOptionPane.showMessageDialog(rootPane, "Bir hata meydana geldi");
            }
        }
                                             
        JFrame f;
        f = new JFrame();
        JOptionPane.showMessageDialog(f, "KAYDINIZ GERCEKLESMISTIR");
        
    }//GEN-LAST:event_jButtonKAYDETActionPerformed

